import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export const signInUser = createAsyncThunk('signInUser', async(body) => {
    const auth = await fetch ("http://localhost:3001/api/v1/user/login", {
        method: "POST",
        body: JSON.stringify(body),
        headers: { "Content-Type": "application/json" },
        })
        const authData = await auth.json()
        localStorage.setItem("token", authData.body.token)
        const userProfile = await fetch ("http://localhost:3001/api/v1/user/profile", {
        method: "POST",
        headers: { "Authorization" : `Bearer ${authData.body.token}`},
        })
        const userData = await userProfile.json()
        return userData;
})

const initialState = {
    firstName: "",
    lastName: "",
}

const userSlice = createSlice({
    name: "user",
    initialState,
    reducers: {
        logout: (state) => {
            state.firstName = ""
            state.lastName = ""
            localStorage.removeItem('token')
        },
        estate: () => {
            console.log(localStorage.getItem('token'))
        }
    },
    extraReducers:{
        [signInUser.pending]: () =>{
            console.log("pending...")
        },
        [signInUser.fulfilled]: (state, action) =>{ 
            console.log("fulfilled") 
            state.firstName = action.payload.body.firstName
            state.lastName = action.payload.body.lastName
        },
        [signInUser.rejected]: (state, action) =>{  
            state.error = action.error.message
        }
    }
})

export const userActions = userSlice.actions
export const slice = userSlice.reducer
