import { configureStore, combineReducers } from "@reduxjs/toolkit";
import { userSliceExp } from "../features/userSlice";
import { authSliceExp } from "../features/authSlice";
import { updateSliceExp } from "../features/updateSlice";
import { signupSliceExp } from "../features/signupSlice";
import storage from "redux-persist/lib/storage";
import { persistReducer } from "redux-persist";

const persistConfig = {
    key: "root",
    version: 1,
    storage
};

const reducer = combineReducers({
    user: userSliceExp,
    auth: authSliceExp,
    signup: signupSliceExp,
    update: updateSliceExp
})

export const store = configureStore({
    reducer: {
        user: userSliceExp,
        auth: authSliceExp,
        signup: signupSliceExp,
        update: updateSliceExp
    }
})
