import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

const userData = localStorage('user')

export const userProfile = createAsyncThunk('userProfile', async() => {
    const response = await fetch ("http://localhost:3001/api/v1/user/profile", {
    method: "POST",
    headers: { "Authorization" : `Bearer ${localStorage.getItem('token')}`},
    })
    const data = await response.json()
    localStorage.setItem('user', data)
    return data;
})

const initialState = {
    firstName: "",
    lastName: "",
}

const userSlice = createSlice({
    name: "user",
    initialState,
    reducers: {

    },
    extraReducers:{
        [userProfile.pending]: () =>{
            console.log("user pending...")
        },
        [userProfile.fulfilled]: (state, action) =>{ 
            console.log("user fulfilled")
            state.firstName = userData.body.firstName
            state.lastName = userData.body.lastName
        },
        [userProfile.rejected]: (state, action) =>{  
            console.log("user rejected") 
            state.error = action.error.message
        }
    }
})

export const userSliceReducer = userSlice.reducer
