import { userProfile } from './features/userSlice'
import { signInUser } from './features/authSlice';
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";

function App(){
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const auth = useSelector((state) => state.auth)
  const user = useSelector((state) => state.user)
  const dispatch = useDispatch();
  function listner(){
    setTimeout(() => {
      dispatch(userProfile())
      console.log("auth is", auth.fulfilled)
    }, 1000);
  }
  function loop(){
    do{
      listner()
    }while(!auth.fulfilled)

  }

  async function handleSubmit(e){
    e.preventDefault()
    dispatch(signInUser({email: "tony@stark.com", password: "password123"}))
    loop()
  }

  async function logout(){
    dispatch({
      type: "user/logout",
    })
    dispatch({
      type: "auth/unfulfill",
    })
  }
  async function state(){
    dispatch({
      type: "user/estate",
    })
    console.log(auth.fulfilled)
    console.log(user)
  }

  return (
  <div>
    <form onSubmit={(e) => handleSubmit(e)}>
      <label>email
        <input type="text" value={email} onChange={(e) => setEmail(e.target.value)}/>
      </label>
      <label>password
      <input type="text" value={password} onChange={(e) => setPassword(e.target.value)}/>
      </label>
      <button type="submit">submit</button>
    </form>
    <button onClick={()=>logout()}>logout</button>
    <button onClick={()=>state()}>state</button>
    {!auth.fulfilled &&<div>im not headers</div>}
    {auth.fulfilled &&<div>im headers</div>}
  </div>
  )
}
export default App

