import { userProfile } from './features/userSlice'
import { signInUser } from './features/authSlice';
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";

function App(){
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const auth  = useSelector((state) => state.auth)
  const dispatch = useDispatch();
  async function handleSubmit(e){
    e.preventDefault()
    dispatch(signInUser({email: "tony@stark.com", password: "password123"}))
    if(auth.fulfilled==true){
      dispatch(userProfile({email: "tony@stark.com", password: "password123"}))
    }
  }
  async function logout(){
    dispatch({
      type: "user/logout",
    })
  }
  async function state(){
    dispatch({
      type: "user/estate",
    })
    console.log(auth.fulfilled)
  }

  return (
  <div>
    <form onSubmit={(e) => handleSubmit(e)}>
      <label>email
        <input type="text" value={email} onChange={(e) => setEmail(e.target.value)}/>
      </label>
      <label>password
      <input type="text" value={password} onChange={(e) => setPassword(e.target.value)}/>
      </label>
      <button type="submit">submit</button>
    </form>
    <button onClick={()=>logout()}>logout</button>
    <button onClick={()=>state()}>state</button>
    {localStorage.getItem('token') &&<div></div>}
  </div>
  )
}
export default App

