import { createSlice, createAsyncThunk, configureStore } from "@reduxjs/toolkit";

const initialState = {
    msg: '',
    error:'',
    message: '',
    id: '',
    email: '',
    loading: false,
}

export const getUserProfile = createAsyncThunk('getUserProfile', async() => {
    const response = await fetch ("http://localhost:3001/api/v1/user/profile", {
        method: "POST",
        headers: { "Authorization" : `Bearer ${localStorage.getItem("token")}`},
        })
        const data = await response.json()
        console.log("data")
        return data;

})

export const signInUser = createAsyncThunk('signInUser', async(body) => {
    const response = await fetch ("http://localhost:3001/api/v1/user/login", {
        method: "POST",
        body: JSON.stringify(body),
        headers: { "Content-Type": "application/json" },
        })
        const data = await response.json()
        localStorage.setItem("token", data.body.token)
        //console.log(data.body.token)
        const userResponse = await fetch ("http://localhost:3001/api/v1/user/profile", {
        method: "POST",
        headers: { "Authorization" : `Bearer ${data.body.token}`},
        })
        data = await userResponse.json()
        //console.log(dato)
        return data;
})

export const userSlice = createSlice({
    name: 'user',
    initialState,
    reducers:{
    },
    extraReducers:{
        [signInUser.pending]: (state) =>{
            console.log("loading ...")
        },
        [signInUser.fulfilled]: (state, action) =>{  
            console.log("fullfiled ...")         
            state.msg = action.payload.message
            state.loading = false
            state.email = action.payload.body.firstName
            state.id = action.payload.body.lastName
        },
        [signInUser.rejected]: (state, action) =>{  
            console.log("rejected ...")          
            state.error = action.error.message
        }
    }
})

export const store = configureStore({
    reducer: {
        user: userSlice.reducer,
    }
})
