import { userProfile } from './features/userSlice'
import { signInUser } from './features/authSlice';
import { signupUser } from './features/signupSlice';
import { useState } from "react";
import { useDispatch, useSelector } from "react-redux";

function App(){
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const auth = useSelector((state) => state.auth)
  const user = useSelector((state) => state.user)
  const newUser = useSelector((state) => state.signup)
  const dispatch = useDispatch();
  let token = localStorage.getItem('token')

  async function signinForm(e){
    e.preventDefault()
    dispatch(signInUser({email: email, password: password}))
    var intrval = setInterval(function() {
      dispatch(userProfile())
      token = localStorage.getItem('token')
      if (token) clearInterval(intrval);
    }, 1000)
  }

  async function signupForm(e){
    e.preventDefault()
    dispatch(signupUser({email: email, password: password, firstName: firstName, lastName: lastName}))
    console.log(newUser)
  }

  async function logout(){
    dispatch({
      type: "user/logout",
    })
    dispatch({
      type: "auth/unfulfill",
    })
  }
  async function state(){
    dispatch({
      type: "user/estate",
    })
    console.log(user)
  }

  return (
  <div>
    <form onSubmit={(e) => signinForm(e)}>
      <label>email
        <input type="text" value={email} onChange={(e) => setEmail(e.target.value)}/>
      </label>
      <label>password
      <input type="text" value={password} onChange={(e) => setPassword(e.target.value)}/>
      </label>
      <button type="submit">signin</button>
    </form>
    <button onClick={()=>logout()}>logout</button>
    <button onClick={()=>state()}>state</button>
    {!auth.fulfilled &&<div>im not headers</div>}
    {auth.fulfilled &&<div>im headers</div>}

    <form onSubmit={(e) => signupForm(e)}>
      <label>firstName
        <input type="text" value={firstName} onChange={(e) => setFirstName(e.target.value)}/>
      </label>
      <label>lastName
        <input type="text" value={lastName} onChange={(e) => setLastName(e.target.value)}/>
      </label>
      <label>email
        <input type="text" value={email} onChange={(e) => setEmail(e.target.value)}/>
      </label>
      <label>password
      <input type="text" value={password} onChange={(e) => setPassword(e.target.value)}/>
      </label>
      <button type="submit">signup</button>
    </form>
  </div>
  )
}
export default App

