import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { useRef } from "react";

export const updateUser = createAsyncThunk('signInUser', async(body) => {
    const update = await fetch ("http://localhost:3001/api/v1/user/profile", {
        method: "PUT",
        body: JSON.stringify(body),
        headers: { "Authorization" : `Bearer ${localStorage.getItem('token')}`, "Content-Type": "application/json"},
        })
        const data = await update.json()
        return data;
})

const initialState = {
    firstName: "",
    lastName: "",
    message: "",
}


const updateSlice = createSlice({
    name: "update",
    initialState,
    reducers: {
    },
    extraReducers:{
        [updateUser.pending]: () =>{
            console.log("update pending...")
        },
        [updateUser.fulfilled]: (state, action) =>{ 
            console.log("update fulfilled")
            const firstName = useRef()
            firstName.current = action.payload.body.firstName
            state.firstName = firstName.current
            state.lastName = action.payload.body.lastName
            state.message = action.payload.message
        },
        [updateUser.rejected]: () =>{ 
            //console.log("update rejected!") 
        }
    }
})

export const updateSliceReducer = updateSlice.reducer
