import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

const signInUser = createAsyncThunk('signInUser', async(body) => {
    const auth = await fetch ("http://localhost:3001/api/v1/user/login", {
        method: "POST",
        body: JSON.stringify(body),
        headers: { "Content-Type": "application/json" },
        })
        const authData = await auth.json()
        localStorage.setItem("token", authData.body.token)
        const userProfile = await fetch ("http://localhost:3001/api/v1/user/profile", {
        method: "POST",
        headers: { "Authorization" : `Bearer ${authData.body.token}`},
        })
        const userData = await userProfile.json()
        return userData;
})

const initialState = {
    firstName: "",
    lastName: "",
}

const userSlice = createSlice({
    name: "user",
    initialState,
    reducers: {
        login: (state, action) => {
            signInUser(action.payload)
            /* state.firstName = auth.firstName
            state.lastName = auth.lastName */
        },
        logout: (state) => {
            state.firstName = ""
            state.lastName = ""
            localStorage.setItem("token", "")
        }
    },
    extraReducers:{
        [signInUser.pending]: (state) =>{
            console.log("loading ...")
        },
        [signInUser.fulfilled]: (state, action) =>{  
            console.log("fullfiled ...")         
            state.msg = action.payload.message
            state.loading = false
            state.email = action.payload.body.firstName
            state.id = action.payload.body.lastName
        },
        [signInUser.rejected]: (state, action) =>{  
            console.log("rejected ...")          
            state.error = action.error.message
        }
    }
})

export const userActions = userSlice.actions
export const slice = userSlice.reducer
